```cpp
#include <iostream>
#include <cstdio>
#include <deque>
#include <list>
#include <set>
using namespace std;
int n,d,k,ans=-1;
struct dian {
  int weizhi;int zuida;
  dian(int w=0,int z=0) {weizhi=w;zuida=z;}
};
dian shuru[500010] ;
//set的比较函数
struct cmp {
  bool operator () (const dian &a ,const dian &b){
    return a.zuida>b.zuida;
  }
};

set<dian,cmp> st;
//判断g能不能取到
bool neng(int g) {
  st.clear();
  st.insert(dian(0,0));
//列举每个输入
  for (int i=1;i<=n;i++) {
    set<dian,cmp>::iterator tmp;
//用set按分数大到小取出
    for (set<dian,cmp>::iterator it=st.begin();it!=st.end();) {
      if (st.empty())
        return false;
//两点大于d+g，删掉
      if (it->weizhi+d+g<shuru[i].weizhi) {
        tmp=it;
        it++;
        st.erase(tmp);
        continue;
      }
//d-g的情况，不能删除，保留，处理比它小的那个
      if (it->weizhi+d-g>shuru[i].weizhi){
        it++;
        continue;
      }
//找到结果
      if (it->zuida+shuru[i].zuida>=k) {
        return true;
      }
      st.insert(dian(shuru[i].weizhi,it->zuida+shuru[i].zuida));
      break;
    }
  }
//没有找到
  return false;
}
//经典的二分递归结构
void erfen(int a,int b) {
  if (a>b) return;
  int x = (a+b)/2;
  if (neng(x))
    {ans=x;erfen(a,x-1);}
  else
    erfen(x+1,b);
}

int main() {
  cin>>n>>d>>k;
  for (int i=1;i<=n;i++) {
    cin>>shuru[i].weizhi>>shuru[i].zuida;
  }
  erfen(0,100000000);
  cout<<ans<<endl;

  return 0;
}
