    int w,v;
    int d;
    bool operator < (const node &ND)const{
        return d < ND.d;
    }
};
int cal(int x,int y){
    return x * x + y * y;
}
vector<node>BG[MAX];
int ct;
map<int,map<int,int> >M;
int main()
{
    int N,V;
    cin >> N >> V;
    for(int i = 1;i <= N;++i){
        int x,y,t,vv;cin >> x >> y >> t >> vv;
        int gcd = __gcd(x,y);
        int tx = x / gcd;
        int ty = y / gcd;
        if(!M[tx][ty])
            M[tx][ty] = ++ct;
        BG[M[tx][ty]].push_back({x,y,t,vv,cal(x,y)});
    }
    for(int i = 1;i <= ct;++i){
        sort(BG[i].begin(),BG[i].end());
        for(int j = 1;j < BG[i].size();++j){
            BG[i][j].w += BG[i][j-1].w;
            BG[i][j].v += BG[i][j-1].v;
        }
    for(int i = 1;i <= ct;++i){
        for(int k = V;k >= 0;--k){
            for(int j = 0;j < BG[i].size();++j){
                if(k - BG[i][j].w < 0)break;
                dp[k] = max(dp[k],dp[k-BG[i][j].w]+BG[i][j].v);
            }
        }
    }
