#include<bits/stdc++.h>
#define rap(i, j, k) for(int i = j; i <= k; i++)
#define db double
#define ll long long
using namespace std;

const int N = 10000 + 10;
map<db, int> MAP;
struct node {
	double ck;
	int x, y;
	int v, w;
	friend bool operator < (const node &A, const node &B) {
		return A.w * B.v > A.v * B.w;
	}//单位时间获得价值大的优先 
}a[N];
ll ans, n, m;

ll DP() {
	map<db, bool> mp;//这条直线第一个点是否选过的map 
	ll lans = 0, lcnt = 0;
	rap(i, 1, n) {
		if(lcnt + a[i].v > m) continue;//如果不能选则跳过 
		if(!mp[a[i].ck]) 
		  if(MAP[a[i].ck] != a[i].x) continue;//如果不是第一个点且第一个点没选过，跳过 
		  else mp[a[i].ck] = true;
		lans += a[i].w;
		lcnt += a[i].v;
	}

	return lans;
}


#define T 190
#define eps 1e-7
#define Delta 0.975

void SA() {//快乐的模拟退火 
	db t = T;
	while(t > eps) {
		int x = 0, y = 0;
		while(x == y) x = rand() % n + 1, y = rand() % n + 1;
		swap(a[x], a[y]);
		int now = DP();
		int dE = ans - now;
		if(dE <= 0) ans = now;
		else if( exp(dE / t) > (db) rand() / (db) RAND_MAX) ;
		else swap(a[x], a[y]);
		t *= Delta;
	}
	return ;
}

#undef T
#undef eps
#undef Delta


#define In(x) scanf("%lld", &x)
#define Out(x) printf("%lld", x)
int main() {
	srand(time(NULL));//随机数初始化 
	In(n);In(m);
	rap(i, 1, n) {
		ll x, y;
		In(x);In(y);In(a[i].v);In(a[i].w);
		a[i].x = x;a[i].y = y;
		a[i].ck = db(y) / db(x);//存储这个点所在直线的斜率 
		//记录直线第一个点  
		if(!MAP[a[i].ck]) MAP[a[i].ck] = x;
		else MAP[a[i].ck] = min(MAP[a[i].ck], int(x));
	}
	//排序，单位时间获得价值大的优先 
	sort(a + 1, a + n + 1);
	rap(i, 1, 15) SA();
	Out(ans);
	return 0;
}
#undef In
#undef Out

