	int id;
}b[o];
struct cmp{
	bool operator()(node a,node b){
		return a.m<b.m;		
	}	
};
bool cmp2(node a,node b){
	return a.fs<b.fs;	
}
priority_queue<node,vector<node>,cmp>q;
int read(){
	int i=1,j=0;
	char ch=getchar();
	while(ch<'0'||ch>'9'){
		if(ch=='-'){
			i=-1;
		}
		ch=getchar();
	}
	while(ch>='0'&&ch<='9'){
		j=j*10+ch-48;
		ch=getchar();
	}
	return i*j;
}
void in(){
    n=read();
    c=read();
    f=read();
	for(int i=1;i<=c;i++){
		a[i].fs=read();
		a[i].m=read();
	}
	sort(a+1,a+c+1,cmp2);			
	for(int i=1;i<=c;i++){
		b[i].zhong=a[i].fs;
		b[i].id=i;
	}
}
void dl(){//left to right
	int money=0;
	for(int i=1;i<=(n/2);i++){
		q.push(a[i]);
		money+=a[i].m;	
	}
	for(int i=(n/2)+1;i<=c-(n/2);i++){
		b[i].m+=money;
		q.push(a[i]);
		money+=a[i].m;
		node x=q.top();
		q.pop();
		money-=x.m;

	}	
	while(!q.empty()){
		q.pop();
	}
}
void dr(){
	int money=0;
	for(int i=c;i>c-(n/2);i--){
		q.push(a[i]);
		money+=a[i].m;	
	}
	for(int i=c-(n/2);i>(n/2);i--){
