		node x=q.top();
		q.pop();
		money-=x.m;
	}	
	while(!q.empty()){
		q.pop();
	}
}
bool cmp3(answer a,answer b){
	return a.zhong>b.zhong;
}
void work(){
	dl();
	dr();
	sort(b+(n/2)+1,b+c-(n/2)+1,cmp3);
	for(int i=(n/2)+1;i<=c-(n/2);i++){
		int key=b[i].id;
		if(b[i].m+a[key].m<f){
			cout<<a[key].fs;
