#include <cstdio>
#include <cctype>
#include <cstring>
template <class T>
void read(T &x)
{
	x=0;char c=getchar();
	while(!isdigit(c)) c=getchar();
	while(isdigit(c)) x=x*10+c-'0',c=getchar();
}
const int N=1010;
const int inf=0x3f3f3f3f;
int res[N],s[N],tot;
void work()
{
	memset(res,0,sizeof res);
	int n,m,ans=0;
	read(n),read(m);
	for(int a,i=1;i<=n;i++)
	{
	    res[s[tot=1]=0]=inf;
	    for(int j=1;j<=m;j++)
		{
			read(a);
			if(a>res[j])
			{
			    int d=a-res[j];
				while(res[s[tot]]<d) d-=res[s[tot]],res[s[tot--]]=0;
				res[s[tot]]-=d;
				res[j]=a;
			}
			s[++tot]=j;
		}
		ans+=inf-res[0];
	}
	printf("%d\n",ans);
}
int main()
{
	int T;read(T);
	while(T--) work();
	return 0;
}
