		x=0;qwq f=1;char c=getchar();
		for(;c<'0'||c>'9';c=getchar())if(c=='-')f=-1;
		for(;c>='0'&&c<='9';c=getchar())x=x*10+c-'0';
		x*=f;
	}
	qwq n;
	QAQ ans;
	inline void solve(){
		read(n);
		ans=(QAQ)n*(QAQ)(n+1)/(QAQ)(2*n-1)/2;
		printf("%.9lf",ans);
	}
}
using namespace Solve;
qwq main(){
	solve();
}


```

