## 完整代码
```cpp
#include <bits/stdc++.h>
using namespace std;
char a[200000][10];
char ans[200001];
int num[10];
int tot;
int main()
{
    int n = 1;
    while(cin >> a[n][1])
    {
        for(int i = 2; i <= 8; i++)
        {
            cin >> a[n][i];
            if(!a[n][i] || (a[n][i] != '1' && a[n][i] != '0'))
            {
                cout << "Error";
                exit(0);
            }
        }
        n++;
    }
    n--;
    num[0] = 1;
    for(int i = 1; i <= 7; i++)
        num[i] = num[i - 1] * 2;
    for(int i = 1; i <= n; i++)
    {
        if(a[i][1] == '1' && a[i][2] == '0' && a[i][3] == '1')
        {
            int ls = 0;
            for(int j = 4; j <= 8; j++)
            {
                ls += (a[i][j] - '0') * num[8 - j];
            }
            if(ls + 'A' > 'Z')
            {
                cout << "Error";
                exit(0);
            }
            ans[++tot] = char(ls + 'A');
        }
        else if(a[i][1] == '1' && a[i][2] == '1' && a[i][3] == '1')
        {
            ans[++tot] = ' ';
        }
        else if(a[i][1] == '0')
        {
            int x = 0, y = 0;
            for(int j = 2; j <= 8; j++)
            {
                x += (a[i][j] - '0') * num[8 - j];
            }
            i++;
            if(i > n || a[i][1] != '0')
            {
                cout << "Error";
                exit(0);
            }
            for(int j = 2; j <= 8; j++)
            {
                y += (a[i][j] - '0') * num[8 - j];
            }
            int now = x / 2 + y / 2;
            int cnt = 1;
            int qaq = now;
            if(now == 0) ans[++tot] = '0';
            while(now)
            {
                cnt *= 10;
                now /= 10;
            }
            cnt /= 10;
            now = qaq;
            while(cnt)
            {
                ans[++tot] = (char)(now / cnt + '0');
                now %= cnt;
                cnt /= 10;
            }
        }
        else
        {
            cout << "Error";
            exit(0);
        }
    }
    cout << (ans + 1) << endl;
}

```

