#include <iostream>
#include <string>
#include <cctype>
#include <cstdlib>
#include <sstream>
using namespace std;

string s, res = "";

bool isBin(string s)
{
	for (int i = 0; i < 8; i++)
	{
		if (s[i] != '0' && s[i] != '1')
			return false;
	}
	return true;
}

int main()
{
	cin >> s;

	// 判断长度是否能被 8 整除
	if (s.size() % 8)
	{
		cout << "Error" << endl;
		return 0;
	}

	int prevNum = 0;
	int flag = 0; // 是否在做加法
	for (int i = 0; i < s.size(); i += 8)
	{
		string temp = s.substr(i, 8);

		// 判断是否只由 0 和 1 组成
		if (!isBin(temp))
		{
			cout << "Error" << endl;
			return 0;
		}

		if (temp[0] == '1' && temp[1] == '0' && temp[2] == '1')
		{
			if (flag)
			{
				cout << "Error" << endl;
				return 0;
			}
			// 把前缀分离出去
			string num = temp.substr(3, 5);
			char c = strtol(num.c_str(), NULL, 2) + 'A';

			// 判断字母是否越界
			if (!isalpha(c))
			{
				cout << "Error" << endl;
				return 0;
			}
			res += c;
		}
		else if (temp[0] == '1' && temp[1] == '1' && temp[2] == '1')
		{
			if (flag)
			{
				cout << "Error" << endl;
				return 0;
			}
			res += ' ';
		}
		else if (temp[0] == '0')
		{
			if (flag)
			{
				prevNum += strtol(temp.c_str(), NULL, 2) / 2;

				// 注意这里用了个 C++ 独有的字符串流拼接数字和字符
				// 这不常用但至少避免了 C 风格字符串和 string 的互相转换
				ostringstream oss;
				oss << res << prevNum;
				res = oss.str();
				flag = 0, prevNum = 0;
			}
			else
			{
				// 标记一下我们在做加法!
				flag = 1;
				prevNum = strtol(temp.c_str(), NULL, 2) / 2;
			}
		}
		else // 如果我们不认识这个前缀
		{
			cout << "Error" << endl;
			return 0;
		}
	}

	// 加法做了一半戛然而止
	if (flag)
		cout << "Error" << endl;
	else
		cout << res << endl;

	return 0;
}
