     for(int i=1;i<=n;i++)scanf("%d",&a[i]);//输入
     int ans=0;//统计
     for(int i=2;i<=n;i++)
     if(a[i]-a[i-1]>t)ans+=t;
     else ans+=a[i]-a[i-1];
