```cpp
#include<bits/stdc++.h>
using namespace std;
int main()//头文件不做解释 
{
	long long n,s=0,l=0,t,a;
	//n：天数 s：总天数 l：高兴的最后一天 t:每次高兴的天数  a:得奖的日期 
	cin>>n>>t;
	//输入天数和高兴时间 
	for(int i=1;i<=n;i++)
	{
		cin>>a;
		if(a>l) s+=t;  
		//判断：若a>l即天数与之前不重复，直接相加 
		if(a<=l) s+=t+a-1-l;   
		//判断：若a<=l即天数与之前重复，去重后相加 
		l=a+t-1;
	}
	cout<<s;//最后完美输出 
	return 0;
}
```
