            ans[y] += (a[x] - map[mp(x, y)]), ans[x] += (a[y] - map[mp(y, x)]);
            map.erase(mp(x, y)), map.erase(mp(y, x));
        }
    }
    for (std::map<std::pair<int, int>, int>::iterator it = map.begin(); it != map.end(); it++)
        ans[it->first.second] += (a[it->first.first] - it->second);
    for (int i = 1; i <= n; i++)
