```cpp
#include <bits/stdc++.h>
using namespace std;

int read(){
    int x=0;
    char c;
    c=getchar();
    while(c<'0'||c>'9')  c=getchar();
    while(c>='0'&&c<='9') x=x*10+(c-'0'), c=getchar();
    return x;
}
void wr(int x){
	if(x>9) wr(x/10);
	putchar(x%10+'0');
	return;
}

int n;
int di[1000001][4];
void myread(){
	n=read();
	for(int i=1;i<=n;i++){
		di[i][0]=read();
		for(int j=1;j<=di[i][0];j++){
			di[i][j]=read();
		}
	}
}

int find_st(){
	for(int i=1;i<=n;i++){
		if(di[i][0]<3){
			return i;
		}
	}
}

int min(int x,int y){
	return x < y ? x : y ;
}

int di_min[1000001];
int find_min(int fa,int v){//返回 v作为子树时的最小字典序（的第一位） 
	if(di_min[v]) return di_min[v];
	if(di[v][0]==1){
		return di_min[v]=v;
	}
	if(di[v][0]==2){
		if(di[v][1]==fa){
			return di_min[v]=min(v,find_min(v,di[v][2]));
		}
		if(di[v][2]==fa){
			return di_min[v]=min(v,find_min(v,di[v][1]));
		}
	}
	if(di[v][0]==3){
		if(di[v][1]==fa){
			return di_min[v]=min(find_min(v,di[v][2]),find_min(v,di[v][3]));
		}
		if(di[v][2]==fa){
			return di_min[v]=min(find_min(v,di[v][1]),find_min(v,di[v][3]));
		}
		if(di[v][3]==fa){
			return di_min[v]=min(find_min(v,di[v][1]),find_min(v,di[v][2]));
		}
	}
}

void pr(int la,int v,bool t){//t=0从儿子来，t=1从爸爸来
	if(t){
		if(di[v][0]==1){
			wr(v);putchar(' '); return;
		}
		if(di[v][0]==2){
			int p = (di[v][1]==la) ? 2 : 1 ;

			if(v<find_min(v,di[v][p])){
				wr(v); putchar(' ');
				pr(v,di[v][p],1);
				return;
			}
			else{
				pr(v,di[v][p],1);
				wr(v); putchar(' ');
				return;
			}
		}
		if(di[v][0]==3){
			int p1,p2;
			if(di[v][1]==la)     {  p1=2; p2=3; }
			else if(di[v][2]==la){	p1=1; p2=3; }
			else                 {  p1=1; p2=2; }

			int f1=find_min(v,di[v][p1]);
			int f2=find_min(v,di[v][p2]);
			if(f1<f2){
				pr(v,di[v][p1],1); wr(v); putchar(' '); pr(v,di[v][p2],1); return;
			}
			else{
				pr(v,di[v][p2],1); wr(v); putchar(' '); pr(v,di[v][p1],1); return;
			}
		}
	}
	else{
		wr(v); putchar(' ');
		if(di[v][0]==2){
			int p = (di[v][1]==la) ? 2 : 1 ;
			if(di[v][p]>find_min(v,di[v][p])){
				pr(v,di[v][p],1); return;
			}
			else{
				pr(v,di[v][p],0); return;
			}
		}
		if(di[v][0]==3){
			int p1,p2;
			if(di[v][1]==la)     {  p1=2; p2=3; }
			else if(di[v][2]==la){	p1=1; p2=3; }
			else                 {  p1=1; p2=2; }
			int f1=find_min(v,di[v][p1]);
			int f2=find_min(v,di[v][p2]);
			if(f1<f2){
				pr(v,di[v][p1],1); pr(v,di[v][p2],0); return;
			}
			else{
				pr(v,di[v][p2],1); pr(v,di[v][p1],0); return;
			}
		}
	}
}

void solve(){
	int st;
	st=find_st();
	cout<<st<<" ";
	if(di[st][0]==1){
		if(di[st][1]>find_min(st,di[st][1])){
			pr(st,di[st][1],1);
		} 
		else{
			pr(st,di[st][1],0);
		}
	}
	else{
		int f1=find_min(st,di[st][1]);
		int f2=find_min(st,di[st][2]);
		if(f1<f2){
			pr(st,di[st][1],1);
			pr(st,di[st][2],0);
		}
		else{
			pr(st,di[st][2],1);
			pr(st,di[st][1],0);
		}
	}
}

int main(){
	myread();
	solve();
	return 0;
} 

```

首先确定 $root$，显然度数 $\leq2$ 的点一定为 $root$。
然后从 $root$ 开始构造子树。

假设下一个点为 $v$，若 $root$ 存在右子树，则其一定位于 $root$ 的右子树中，需对其右儿子进行操作。
若其存在右儿子，则可以 $dp$ 求出子树内度数 $\leq2$ 的最小点 $f_v$ 作为右儿子。

若其不存在右儿子，则判断 $v$ 与 $f_v$ 的大小选择最优解，若其相等，则一定为右儿子。

复杂度 $O(n)$ 

### code

```cpp
#include<iostream>
#include<cstdio>
#include<vector>
#include<algorithm>
using namespace std;
inline int read(){
    int x=0,f=1;
    char ch=getchar();
    while(ch<'0'||ch>'9'){
        if(ch=='-') f=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9'){
        x=(x<<1)+(x<<3)+(ch^48);
        ch=getchar();
    }
    return x*f;
}
const int N=1e6+5;
int n,rt,f[N];
vector<int> g[N];
void dp(int u,int fa=0){
    f[u]=(g[u].size()<=2?u:0x3f3f3f3f);
    for(int v:g[u]) if(v!=fa){
        dp(v,u);
        f[u]=min(f[u],f[v]);
    }
}
bool cmp(int x,int y){
	return f[x]<f[y];
}
void dfs(int u,int fa,int check=0){
	bool flag=true;
	sort(g[u].begin(),g[u].end(),[](int x,int y){return f[x]<f[y];});
	if(check){
		for(int v:g[u]){
			if(v!=fa){
				if(g[u].size()<=2){
					if(f[v]>u){
						printf("%d ",u);
						flag=false;
					}
					dfs(v,u,1);
				}
				else{
					dfs(v,u,1);
					if(flag){
						printf("%d ",u);
						flag=false;
					}
				}
			}
		}
		if(flag) printf("%d ",u);
		return ;
	}
	printf("%d ",u);
	for(int v:g[u]){
		if(v!=fa){
			if(g[u].size()==2) dfs(v,u,v>=f[v]);
			else if(g[u].size()==3) dfs(v,u,check--);
		}
	}
}
int main(){
    n=read();
    for(int i=1;i<=n;i++){
        int k=read();
        if(k<=2&&!rt) rt=i;
        while(k--){
			int a=read();
			g[i].push_back(a);
		}
    }
	g[rt].push_back(rt);
    dp(rt);
    dfs(rt,rt);
    return 0;
}
```
