#include <iostream>
#include <cstdlib>
#include <cstdio>
#include <cmath>
#include <queue>
#include <string>
#include <cstring>
#include <algorithm>
using namespace std;

const int Max=101;
bool mp[Max][Max];
bool H[Max][Max][11];//简易hash 
int dx[4]={0,0,1,-1};
int dy[4]={1,-1,0,0};//方向 
int N,K,A,B,C;
struct node
{
	int x,y;//坐标 
	int oil;//油量 
	int pay;//花费 
	friend bool operator <(node a,node b)
	{
		return a.pay>b.pay;
	}//优先队列 
};

int bfs()
{
	node tmp,nx;
	priority_queue<node> Q;
	Q.push((node){1,1,K,0});//初始化起点 
	while(!Q.empty())
	{
		tmp=Q.top();Q.pop();
		if(H[tmp.x][tmp.y][tmp.oil])continue;
		H[tmp.x][tmp.y][tmp.oil]=true;//hash表判重 
		if(tmp.x==N&&tmp.y==N)	return tmp.pay;//到达终点 
		for(int i=0; i<4; i++)
		{
			nx=tmp;
			nx.x=nx.x+dx[i],nx.y=nx.y+dy[i],nx.oil--;//改变坐标，减少油量 
			if(nx.oil<0||nx.x<0||nx.y<0||nx.x>N||nx.y>N)	continue;//没油或越界就退出 
			if(nx.x<tmp.x||nx.y<tmp.y)	nx.pay+=B;//往回走就付B元 
			if(mp[nx.x][nx.y])	nx.oil=K,nx.pay+=A;//到加油站就（一定要）付钱A元并加油 
			Q.push(nx);//将上种情况入队 
			if(!mp[nx.x][nx.y]&&nx.oil<K)	nx.oil=K,nx.pay+=A+C;//没加油站就造加油站,付钱A+C元并加油
			Q.push(nx);//将上种情况入队 
		}
	}
	return -1;
}

int main()
{
	memset(H,false,sizeof(H));
	memset(mp,false,sizeof(mp));
	scanf("%d%d%d%d%d",&N,&K,&A,&B,&C);
	for(int i=1; i<=N; i++)
		for(int j=1; j<=N; j++)
			cin>>mp[i][j];//输入 
	cout<<bfs()<<endl;//输出 
	return 0;
}
