		if(u.sum>=f[u.x][u.y][u.dis])continue;
		f[u.x][u.y][u.dis]=u.sum;
		for(int i=0;i<4;i++){
			int nx=u.x+dx[i];
			int ny=u.y+dy[i];
			if(!check(nx,ny))continue;
			if(G[u.x][u.y])q.push(node{nx,ny,K-1,u.sum+cost[i]+A});
			else if(!u.dis)q.push(node{nx,ny,K-1,u.sum+cost[i]+A+C});
			else q.push(node{nx,ny,u.dis-1,u.sum+cost[i]});
		}
	}
}

int main(){
	//freopen("in.txt","r",stdin);
	memset(f,0x7f,sizeof(f));
	scanf("%d%d%d%d%d",&N,&K,&A,&B,&C);
	cost[0]=cost[1]=B;
	for(int i=1;i<=N;i++)
	for(int j=1;j<=N;j++)
	scanf("%d",&G[i][j]);
	BFS();
	for(int i=0;i<=K;i++)
	ans=min(ans,f[N][N][i]);
	printf("%d",ans);
	return 0;
}
```

设d[i][j][k]表示到(i,j)用k升油所需的最小费用，跑SPFA的队列也有这三个变量。
初始化起点满油时费用0。
```pascal
  SPFA分三步：
  1. 强制加油新状态
  2. 自行加油新状态
  3. 模拟开车
```
然后枚举到终点时油量找答案。  
详细的请$\color{white}\colorbox{white}{不要}$  看代码:
```cpp
#include<cstdio>
#include<cstring>
#define inf 0x7fffffff
#define pd(x,y) (x&&x<=n&&y&&y<=n)//判断越界
#define mes(x,y) memset(x,y,sizeof(x))
#define min(x,y) (x<y?x:y)
using namespace std;
const int dx[4]={0,-1,0,1};//方向
const int dy[4]={-1,0,1,0};
inline int qr()//快读
{
    int x=0,f=1;char c=getchar();
    while(c<'0'||'9'<c){c=='-'?f=-1:1;c=getchar();}
    while('0'<=c&&c<='9'){x=x*10+(c&15);c=getchar();}
    return x*f;
}
struct node
{
    int x,y,c;
}q[10001];int hd,tl;//队列
int n,K,A,B,C,ans=inf;
int d[101][101][11];
bool mp[101][101],bk[101][101][11];
int main()
{
    n=qr();K=qr();A=qr();B=qr();C=qr();
    for(int i=1;i<=n;i++)
        for(int j=1;j<=n;j++)
            mp[i][j]=qr();
    mes(d,63);d[1][1][K]=0;
    bk[1][1][K]=1;
    hd=1;tl=2;q[1]=(node){1,1,K};
    while(hd!=tl)
    {
        int x=q[hd].x,y=q[hd].y,c=q[hd].c;
        bk[x][y][c]=0;
        hd++;if(hd>10000)hd=1;
        if(x==n&&y==n)continue;//到终点不可能再走
        if(mp[x][y]&&c<K)//如果有油站必须加油
        {//油满表示这个状态是上一次在这进入队列的，所以不能再加油
        	int dis=d[x][y][c]+A;//油费
        	if(d[x][y][K]>dis)
        	{
        		d[x][y][K]=dis;
        		if(!bk[x][y][K])
        		{
        			bk[x][y][K]=1;
        			q[tl++]=(node){x,y,K};
        			if(tl>10000)tl=1;
				}
			}
			continue;//因为强制加油所以当前还未加油的状态废了
		}
		else//否则再新建一个自行加油的状态
		{
			int dis=d[x][y][c]+A+C;//建站费
			if(d[x][y][K]>dis)
			{
				d[x][y][K]=dis;
				if(!bk[x][y][K])
				{
					bk[x][y][K]=1;
					q[tl++]=(node){x,y,K};
					if(tl>10000)tl=1;
				}
			}
		}
        if(c)//有油开车
        for(int t=0;t<4;t++)
        {
			int xx=x+dx[t];
			int yy=y+dy[t];
			if(!pd(xx,yy))continue;
			int dis=d[x][y][c];
			if(t<2)dis+=B;//前两个状态是往回走的需要加费
			if(d[xx][yy][c-1]>dis)//耗1 油
			{
				d[xx][yy][c-1]=dis;
				if(!bk[xx][yy][c-1])
				{
					bk[xx][yy][c-1]=1;
					q[tl++]=(node){xx,yy,c-1};
					if(tl>10000)tl=1;
				}
			}
        }
    }
    for(int i=0;i<=K;i++)ans=min(ans,d[n][n][i]);
    printf("%d\n",ans);
    return 0;
}
