        read=x;
    }
    int dfs(int st){
        if(!du[st])return 1;
        if(sa[st])return sa[st];
        int sum=0;
        for(Re int i=head[st];i;i=edge[i].nex)
            sum=(sum+dfs(edge[i].to))%Mod;//每加一个数就%一次
        sa[st]=sum%Mod;
        return sa[st];
    }
    int main(){
        in(n);in(m);
        int a,b;
        for(Re int i=1;i<=m;i++){
            in(a);in(b);
            inu[b]++;du[a]++;
