```
#include <bits/stdc++.h>

using namespace std;
const int N = 10001, mod = 80112002;

int n, m, indeg[N], outdeg[N], a, b, f[N], ans;

int map1[N][N];

queue<int> q;
int main()
{
    cin >> n >> m;
    for (int i = 1; i <= m; i++)
    {
        cin >> a >> b;
        map1[a][b] = 1;
        outdeg[a]++;
        indeg[b]++;
    }
    for (int i = 1; i <= n; i++)
        if (indeg[i] == 0)
            f[i] = 1, q.push(i);
    while (!q.empty())
    {
        int tp = q.front();
        q.pop();
        for (int to = 1; to <= n; to++)
        {
            if (map1[tp][to] == 0)
                continue;
            (f[to] += f[tp]) %= mod;
            indeg[to]--;
            if (indeg[to] == 0)
                q.push(to);
            if (indeg[to] == 0 && outdeg[to] == 0)
            {
                (ans += f[to]) %= mod;
                continue;
            }
        }
    }
    cout << ans << endl;
}
