void add(int x,int y)
{
	a[++ sum].y = y;
	a[sum].ne = head[x];
	head[x] = sum;
}
int ru[Max >> 2],chu[Max >> 2];
int dp[Max >> 2];
int ans = 0;

int dfs(int x)
{
	if(dp[x] != 0)return dp[x];
	int ans = 0;
	if(ru[x] != 0 && chu[x] == 0)
		ans ++;
	for(register int i = head[x];i != 0;i = a[i].ne)
	{
		ans += dfs(a[i].y);
	}
	dp[x] = ans % mo;
	return ans % mo;
}

