```cpp
#include<bits/stdc++.h>
using namespace std;
string qaq;
int n,m,a[27][2],b[27];
int main(){
	scanf("%d%d",&n,&m);
	for(int W=1;W<=n;W++){
		memset(a,0,sizeof(a));
		cin>>qaq;//读入
		int e=0,qwq,eee=0;
		int l=qaq.length();
		for(int i=0;i<l;i++){
			if(qaq[i]=='=') {
				e=1;//判断此时处于等式哪一边
				continue;
			}
			if(qaq[i]=='+') continue;
			if(qaq[i]=='?'){//判断需要填补的化学式在等式的哪一边
				if(e==0) qwq=0;
				if(e==1) qwq=1;
			}
			if(qaq[i]>='A'&&qaq[i]<='Z'&&m!=0&&m!=2){//元素符号
				if(i+2<l&&qaq[i+2]<='9'&&qaq[i+2]>='0'&&qaq[i+1]<='9'&&qaq[i+1]>='0'){
					eee=1;//如果给出的化学式元素下标超过9就果断break
					break;
				}
				if(qaq[i+1]=='+'||qaq[i+1]=='='||i==l-1){
					a[qaq[i]-64][e]++;//如果下一个字符不是数字，代表这种原子只有一个
				}
				else a[qaq[i]-64][e]=a[qaq[i]-64][e]+qaq[i+1]-48,


				i++;//否则加上这种原子的个数，并且记得跳过下一个

			}
			if(qaq[i]>='A'&&qaq[i]<='Z'&&m!=1&&m!=3){
				if(i+2<l&&qaq[i+2]<='9'&&qaq[i+2]>='0'&&qaq[i+1]<='9'&&qaq[i+1]>='0'){
					eee=1;
					break;
				}
				if(qaq[i+1]<='Z'&&qaq[i+1]>='A'){
					a[qaq[i]-64][e]++;
				}
				else if(qaq[i+1]=='+'||qaq[i+1]=='='||i==l-1){
					a[qaq[i]-64][e]++;
				}
				else{
					a[qaq[i]-64][e]=a[qaq[i]-64][e]+qaq[i+1]-48;
				}
			}
		}
		if(eee==1){
			printf("No Solution\n");//如果元素下标超过9，说明不符合要求
			continue;
		}
		for(int i=1;i<=26;i++){
			b[i]=a[i][0]-a[i][1];//计算相减的值
		}
		int p=0,hh=0;
		if(qwq==0){//需要求的化学式在等号左边
			for(int i=1;i<=26;i++){
				if(b[i]>0) {
					p=1;//因为需要求的化学式在左边，所以当左边某一元素的原子数大于右边的时，无解
					break;
				}
				if(b[i]==0) hh++;//每当元素的原子个数为0时++
				b[i]=abs(b[i]);
				if(b[i]>=10){
					eee=1;//所求的化学式某一元素的原子个数超过9
					break;
				}
			}
			if(eee==1){
				printf("No Solution\n");
				continue;
			}
			if(hh==26) p=1;//如果都为0，则化学式不存在，同样无解
			if(p==1){
				printf("No Solution\n");
				continue;
			}
			for(int i=1;i<=26;i++){
				if(b[i]!=0){
					char owo=i+64;
					printf("%c",owo);
				}
				if(b[i]>1){
					printf("%d",b[i]);
				}
			}
		}
		if(qwq==1){
			for(int i=1;i<=26;i++){
				if(b[i]<0) {
					p=1;//因为需要求的化学式在右边，所以当左边某一元素的原子数小于右边的时，无解
					break;
				}
				if(b[i]==0) hh++;//所求的化学式某一元素的原子个数超过9
				b[i]=abs(b[i]);
				if(b[i]>=10) {
					eee=1;//所求的化学式某一元素的原子个数超过9
					break;
				}
			}
			if(eee==1){
				printf("No Solution\n");
				continue;
			}
			if(hh==26) p=1;//如果都为0，则化学式不存在，同样无解
			if(p==1){
				printf("No Solution\n");
				continue;
			}
			for(int i=1;i<=26;i++){
				if(b[i]!=0){
					char owo=i+64;
					printf("%c",owo);
				}
				if(b[i]>1){
					printf("%d",b[i]);
				}
			}
		}
		printf("\n");
	}
	return 0;
}

