#include<bits/stdc++.h>
using namespace std;
long long n,m,T,a[510][510],c[510][510][510],b[510][510];
int main(){
	scanf("%lld%lld%lld",&n,&m,&T);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			scanf("%lld",&a[i][j]);
		}
	}
	for(int W=1;W<=T;W++){
		int x,y,k,p=0;
		scanf("%d%d%d",&x,&y,&k);
		for(int i=x;i<x+k-1;i++){
			for(int j=y;j<y+k-1;j++){
				if(a[i][j]+a[i+1][j+1]!=a[i+1][j]+a[i][j+1]){
					p=1;
					break;
				}
			}
			if(p==1) break;
		}
		if(p==1){
			printf("N\n");
			continue;
		}
		printf("Y\n");
	}
	return 0;
}

```

