}e[20005]; 
int cnt,head[55],f[55][105][2005],g[55][105][2005],L[55],M[55],P[55],n,m;
int ru[55],h[55][2005];
void add(int u,int v,int w)
{
	cnt++;
	e[cnt].to=v;
	e[cnt].nxt=head[u];
	head[u]=cnt;
	e[cnt].w=w;
	ru[v]++;
}
void dp(int u)
{
	if(!head[u])
	{//f[i][j][k]表示第i个物品，有j个用于合成上层，花费为k的最小代价 
		L[u]=min(L[u],m/M[u]);
		for(int i=0;i<=L[u];i++)//枚举上交i个 
			for(int j=i;j<=L[u];j++)//购买j个 
				f[u][i][j*M[u]]= (j-i)*P[u]; 
		return;
	}
	L[u]=0x3f3f3f3f;
	for(int i=head[u];i;i=e[i].nxt)
	{
		int v=e[i].to;
		dp(v);
		L[u]=min(L[u],L[v]/e[i].w);
		M[u]+=e[i].w*M[v];
	}
	L[u]=min(L[u],m/M[u]);
	g[u][0][0]=0;
	for(int l=L[u];l>=0;l--)
	{
		int tot=0;
		for(int i=head[u];i;i=e[i].nxt)
		{
			tot++;
			int v=e[i].to;
