				for(int k=0;k<=j;k++)
					g[u][tot][j]=max(g[u][tot][j],g[u][tot-1][j-k]+f[v][l*e[i].w][k]);
		}
		for(int j=0;j<=l;j++)
			for(int k=0;k<=m;k++)
				f[u][j][k]=max(f[u][j][k],g[u][tot][k]+(l-j)*P[u]);
	}
 } 
int main()
