    return dinic()>=m;
}

bool pd(node a,node b,node c,bool ok)
{
    if(ok)//没树 
    {
        if(a.r*a.r<(a.x-b.x)*(a.x-b.x)+(a.y-b.y)*(a.y-b.y)) return false;// 直接判断能不能攻击到 
	else true;
