    else//有树 
    {
	if(a.r*a.r<(a.x-b.x)*(a.x-b.x)+(a.y-b.y)*(a.y-b.y)) return false; //先判断能不能攻击到 
        ll A=b.y-a.y,
           B=a.x-b.x,
