	for (int i=0;i<=x;i++)
	  c[i]=0;
	for (int i=1;i<=N;i++)
	  c[Rank[Temp[i]]]++;
	for (int i=1;i<=x;i++)
	  c[i]+=c[i-1];
	for (int i=N;i>=1;i--)
	  SA[c[Rank[Temp[i]]]--]=Temp[i];
}
void BuildSA()
{
   for (int i=1;i<=N;i++)
     Rank[i]=a[i],Temp[i]=i;
   Build(M=300);
   for (int T=1;T<N;T*=2)
     {
	int p=0;
	for (int i=N-T+1;i<=N;i++)
	  Temp[++p]=i;
	for (int i=1;i<=N;i++)
	  if (SA[i]>T) 
	    Temp[++p]=SA[i]-T;
	Build(M=p);
	swap(Rank,Temp);
	Rank[SA[1]]=1;
	p=1;
	for (int i=2;i<=N;i++)
	    {
	    if (Temp[SA[i]]==Temp[SA[i-1]]&&Temp[SA[i]+T]==Temp[SA[i-1]+T]) Rank[SA[i]]=p;
	    else Rank[SA[i]]=++p;
		}
	}
}
