#define re register
#define in inline
#define pi acos(-1.0)
#define inf 2147483640
#define cd complex<double>
using namespace std;
in int read()
{
    int sum=0,negative=1;
    char ch=getchar();
    while(ch<'0'||ch>'9')
    {
        if(ch=='-')negative=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9')
    {
        sum=sum*10+ch-'0';
        ch=getchar();
    }
    return sum*negative;
}
in void write(int X)
{
    if(X<0)putchar('-'),X=-X;
    if(X>=10)write(X/10);
    putchar(X%10+'0');
}
const int maxn=15e4;
struct node{
	int t1,t2;
}a[maxn];
priority_queue<int>q;
int n,ans;
long long t;
bool cmp(node x,node y){
	return x.t2<y.t2;
}
int main()
{
	n=read();
	for(re int i=1;i<=n;++i){
		a[i].t1=read();
		a[i].t2=read();
	}
	sort(a+1,a+n+1,cmp);
	for(re int i=1;i<=n;++i){
		if(a[i].t2>=t+a[i].t1){
			++ans;
			t+=a[i].t1;
			q.push(a[i].t1);
		}
		else{
			if(q.size()&&a[i].t1<q.top()){
				t-=q.top();
				t+=a[i].t1;
				q.pop();
				q.push(a[i].t1);
			}
		}
	}
	write(ans);
    return 0;
}
