```cpp
#include<bits/stdc++.h>
using namespace std;
long long a,b,c,aa[100001],bb[100001],cc[100001],ans=1,k,ansk;
int main(){
	cin>>a>>b>>c;
	for(int i=2;i*i<=a;i++){
		if(a%i==0){
			aa[i]++;
			a/=i;
			i--;
		}
	}
	if(a!=1) aa[a]++;
	for(int i=2;i*i<=b;i++){
		if(b%i==0){
			bb[i]++;
			b/=i;
			i--;
		}
	}
	if(b!=1) bb[b]++;
	for(int i=2;i*i<=c;i++){
		if(c%i==0){
			cc[i]++;
			c/=i;
			i--;
		}
	}
	if(c!=1) cc[c]++;
	for(int i=2;i<=100000;i++){
		k=max(aa[i],bb[i]);
		ansk=max(k,cc[i]);
		for(int j=1;j<=ansk;j++){
			ans*=i;
		}
		k=0;
		ansk=0;
	}
	cout<<ans;
	return 0;
}
```

