#include<iostream>
#include<cmath>
#include<cstring>
#include<cstdio>
#include<algorithm>
using namespace std;
int h[200200],a[200200];
unsigned long long n,m,s,i,j,k,minn=0,maxn=0,l,r,q;

inline int rd(){
    int x=0;
    bool f=true;
    char c;
    c=getchar();
    while(c<'0'||c>'9'){
        if(c=='-') f=false;
        c=getchar();
    }
    while(c>='0'&&c<='9'){
        x=(x<<1)+(x<<3)+(c^48);
        c=getchar();
    }
    return f?x:-x;
} 

bool valid(unsigned long long x){
    unsigned long long sum=0;
    bool flag=1;
    for(i=1;i<=n;++i)
        if(h[i]+a[i]*x>=minn){
            if(flag)sum+=h[i]+a[i]*x;
            if(sum>=s)return true;
    }
    return false;
}

int main(){
    scanf("%lld%lld%lld",&n,&s,&minn);
    for(i=1;i<=n;++i)h[i]=rd();
    for(i=1;i<=n;++i)a[i]=rd();
    q=max(minn,s/n+1);
    for(i=1;i<=n;++i)if(h[i]<q)maxn=max(maxn,(q-h[i])/a[i]+1);  
    l=0;r=maxn;
    while(l<r){
        unsigned long long mid=(l+r)/2;
        if(valid(mid))r=mid;
        else l=mid+1;
    }
    printf("%lld\n",l);
    return 0;
}
