	for(int i=3; i<=1000000; i++)
		d[i] = (ll)(i-1) * (d[i-1] + d[i-2]) % mod;
	for(int i=2; i<=1000000; i++){
		fac[i] = (ll)fac[i-1] * i % mod;
		inv[i] = (ll)(mod - mod / i) * inv[mod%i] % mod;
	}
	for(int i=2; i<=1000000; i++)
		inv[i] = (ll)inv[i-1] * inv[i] % mod;
