#include<bits/stdc++.h>
using namespace std;
int n,len;
int han(int x){//计算荒谬值的函数 
	int res=0,last;
	while(x&&x%10==0)x/=10;
	last=x%10;
	while(x){
		res+=2;
		x/=10;
	}
	if(last==5)res--;
	return res;
}
int find(int l,int r){
	int ans,minn=0x3f3f3f3f;
	if(r-l<=len){//区间长度小的，暴力枚举 
		for(int i=l;i<=r;i++){
			int x=han(i);
			if(x<minn)minn=x,ans=i;
		}
	}
	else {
		int i=l,j=r;
		while(i%len)i++;//先把区间两边都跳到len的整数倍 
		while(j%len)j--;
		for(int k=l;k<i;k++){//两边多余的暴力计算 
			int x=han(k);
			if(x<minn)minn=x,ans=k;
		}
		for(int k=i/len;k<=j/len;k++){//中间每次跳len的长度，只用看len整数倍的数 
			int x=han(k);
			if(x<minn)minn=x,ans=k*len;
		}
		for(int k=j+1;k<=r;k++){
			int x=han(k);
			if(x<minn)minn=x,ans=k;
		}
	}
	return ans;
}
int main(){
