#endif
	n=read();m=read();
	for(re int i=1;i<=n;i++){
		for(re int j=1;j<=m;j++){
			cin>>a[i][j];
			if(a[i][j]=='.')id[i][j]=++cnt;
		}
	}

	W=matrix(cnt,cnt);//初始化矩阵
	D=matrix(cnt,cnt);
	A=matrix(cnt,cnt);

	for(re int i=1;i<=n;i++){//构造邻接矩阵
		for(re int j=1;j<=m;j++){
