inline int find_kth(int opl,int opr,int opk) {
	static int fbk[318],fnm[100505]; int sum=0,ret=-1;
	if (pb[opl]==pb[opr]) {
		for (int i=opl;i<=opr;++i) {
			++fbk[pa[a[find_fat(i)]]];
