						ret=j; break;
					}
				break;
			}
		for (int i=opl;i<=opr;++i) {
			--fbk[pa[a[find_fat(i)]]];
			--fnm[a[find_fat(i)]];
		}
		return ret;
	}
	for (int i=opl;pb[i]==pb[opl];++i) {
		++fbk[pa[a[find_fat(i)]]];
		++fnm[a[find_fat(i)]];
	}
	for (int i=opr;pb[i]==pb[opr];--i) {
		++fbk[pa[a[find_fat(i)]]];
		++fnm[a[find_fat(i)]];
	}
	for (int i=0;i<qa;++i)
		if ((sum+=sbk[pb[opr]-1][i]-sbk[pb[opl]][i]+fbk[i])>=opk) {
			for (int j=(i+1)*qa-1;;--j)
				if ((sum-=snm[pb[opr]-1][j]-snm[pb[opl]][j]+fnm[j])<opk) {
					ret=j; break;
				}
			break;
		}
	for (int i=opl;pb[i]==pb[opl];++i) {
		--fbk[pa[a[find_fat(i)]]];
		--fnm[a[find_fat(i)]];
	}
	for (int i=opr;pb[i]==pb[opr];--i) {
		--fbk[pa[a[find_fat(i)]]];
		--fnm[a[find_fat(i)]];
	}
	return ret;
}

