            		if(!fktmp[fasz/n2])fkdl[fkdlt++]=fasz/n2;
            		sztmp[fasz]+=1;
            		fktmp[(fasz)/n2]+=1;
            	}
            	for(int i=1;i<=r%n2+1;++i)
                {
                    int fa=fifa(rr,i);
                    int fasz=fk[rr][fa]-1;
            		if(!sztmp[fasz])szdl[szdlt++]=fasz;
            		if(!fktmp[fasz/n2])fkdl[fkdlt++]=fasz/n2;
                    sztmp[fasz]+=1;
            		fktmp[(fasz)/n2]+=1;
                }
                int j=0;
                qwe=fktmp[0]+fkqzh[0].query2(ll+1,rr);
                while(pm+qwe<=k)
                {
                	pm+=qwe;
                	++j;
                	qwe=fktmp[j]+fkqzh[j].query2(ll+1,rr);
                }
                int j2=j*n2;
                qwe=sztmp[j2]+szqzh[j2].query2(ll+1,rr);
                while(pm+qwe<=k)
                {
                	pm+=qwe;
                	++j2;
                	qwe=sztmp[j2]+szqzh[j2].query2(ll+1,rr);
                }
                ans=j2+1;
            }
            else
            {
            	for(int i=l%n2+1;i<=r%n2+1;++i)
            	{
            		int fa=fifa(ll,i);
            		int fasz=fk[ll][fa]-1;
            		if(!sztmp[fasz])szdl[szdlt++]=fasz;
            		if(!fktmp[fasz/n2])fkdl[fkdlt++]=fasz/n2;
            		sztmp[fasz]+=1;
            		fktmp[(fasz)/n2]+=1;
            	}
            	int j=0;
            	qwe=fktmp[0];
            	while(pm+qwe<=k)
            	{
            		pm+=qwe;
            		++j;
            		qwe=fktmp[j];
            	}
            	int j2=j*n2;
            	qwe=sztmp[j2];
            	while(pm+qwe<=k)
            	{
            		pm+=qwe;
            		++j2;
            		qwe=sztmp[j2];
            	}
            	ans=j2+1;
            }
            for(int i=1;i<szdlt;++i)sztmp[szdl[i]]=0;
            for(int i=1;i<fkdlt;++i)fktmp[fkdl[i]]=0;
            write(ans);
            putchar('\n');
        }
    }
    return 0;
}
