typedef long long ll;
const int N = 1005;
map<ll, ll> f[N];
ll fib[N], sum[N], n;
ll dfs(ll n, int m) {
	if (n < 0) return 0; // 负数，不行
	if (!m || !n) return !n ? 1 : 0; // 选完或中途剩下 0 就返回
	if (f[m].count(n)) return f[m][n]; // 记忆化
	if (n >= sum[m - 1]) return f[m][n] = dfs(n - fib[m], m - 1); // 剪枝qwq
