}d[2005];
int cmp(const ben &x,const ben &y)
{
	return x.a>y.a;
}
int f[2005][2005];
int main()
{
	int n;
	scanf("%d",&n);
	int m=0;
	for(int i=0;i<=n;i++)
	{
		f[0][i]=f[i][n+1]=minx;
	}
	f[0][1]=0;
	for(int i=1;i<=n;i++)
	{
		scanf("%d%d",&d[i].a,&d[i].b);
	}
	sort(d+1,d+n+1,cmp);
	for(int i=1;i<=n;i++)
	{
		for(int j=0;j<=n;j++)
		{
			f[i][j]=max(f[i-1][j],f[i-1][max(j-d[i].a,0)+1]+d[i].b);
		}
	}
	int ans=-2000000000;
	for(int i=0;i<=n;i++)
