using namespace std;

int read() {
	char ch=getchar();
	int f=1,x=0;
	while(ch<'0'||ch>'9') {
		if(ch=='-')
			f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9') {
		x=x*10+ch-'0';
		ch=getchar();
	}
	return f*x;
}

const int MAXN=5e4+10;

int n,m,cnt;
int p[100],h[MAXN],father[MAXN],dis[MAXN];

int find(int x) {
	if(x!=father[x]) {
		return father[x]=find(father[x]);
	}
	return father[x];
}

void insert(int x) {
	for(int i=63;i>=0;i--) {
		if(!(x&(1ll<<i))) {
			continue;
		}
		if(!p[i]) {
			p[i]=x;
			break;
		}
		x=p[i]^x;
	}
}

struct edge {
	int u,v,w,next;
	bool tree;
}e[MAXN<<2];

void addedge(int u,int v,int w) {
	e[++cnt].u=u;
	e[cnt].v=v;
	e[cnt].w=w;
	e[cnt].next=h[u];
	h[u]=cnt;
}

void generate_tree() {
	for(int i=1;i<=m;i++) {
		if(find(e[i].u)!=find(e[i].v)) {
			father[find(e[i].u)]=find(e[i].v);
			e[i].tree=1;
			e[i+1].tree=1;
		}
	}
}
void dfs(int u,int fa) {
	for(int i=h[u];i;i=e[i].next) {
		if(!e[i].tree) {
			continue;
		}
		int v=e[i].v;

		if(v!=fa) {
			dis[v]=dis[u]^e[i].w;
			dfs(v,u);
		}
	}
}

signed main() {
	cin>>n>>m;
	for(int i=1;i<=n;i++) {
		father[i]=i;
	}
	for(int i=1;i<=m;i++) {
		int u,v,w;
		u=read();
		v=read();
		w=read();
		addedge(u,v,w);
		addedge(v,u,w);
	}
	m<<=1;

	generate_tree();
	dfs(2,0);

	for(int i=1;i<=m;i++) {
		if(!e[i].tree) {
			insert(dis[e[i].u]^dis[e[i].v]^e[i].w);
		}
	}

	int ans=dis[1]^dis[n];
	for(int i=63;i>=0;i--) {
		ans=max(ans,ans^p[i]);
