	for (int k = 1; k <= 20; k++)
		for (int i = 1; i <= len; i++) go[i][k] = go[go[i][k - 1]][k - 1];
	for (int i = 1; i <= n; i++) {
		int l = a[i].l, r = a[i].r, lst, ans = 1;
		if (l > r) r += m;
		r = gt(r); lst = l + m;
		for (int k = 20; k >= 0; k--)
			if (que[go[r][k]] < lst) ans += (1 << k), r = go[r][k];
