			}
		}
	}
	if(type==-1) for(int i=0;i<len;i++) a[i]=1ll*a[i]*inv[len]%MOD;
}
int main(){
	scanf("%d%d%s%s",&m,&n,s+1,t+1);ipr=qpow(pr,MOD-2);
