	if( gaycnt[pos] < selcnt ) return 0;
	For(i,upp)
		if(sel[i]&&!gay[i][pos])
			return 0;
	return 1;
}
int ans = 0;
void Dfs( int pos , int tans , int cnt ){
	if( pos == n+1 ){
		Mymax(ans,tans);
		return;
	}
	if( tans + n-pos+1 <= ans ) return;
	int pmax = tans;
	Forx(i,pos,n)
		pmax += (int)Cansel(i,pos-1,cnt);
	if( pmax <= ans ) return;
	sel[pos] = 0;
	Dfs(pos+1,tans,cnt);
	if( Cansel(pos,pos-1,cnt) ){
		sel[pos] = 1;
		Dfs(pos+1,tans+1,cnt+1);
		sel[pos] = 0;
	}
}
