inline void sieve()
{
	fill(prime,prime+MAXN,1);
	mu[1]=1,phi[1]=1,pcnt=0;
	for(rg int i=2;i<MAXN;++i)
	{
		if(prime[i])
		{
			prime[++pcnt]=i,mu[i]=-1,phi[i]=i-1;
		}
		for(rg int j=1;j<=pcnt&&i*prime[j]<MAXN;++j)
		{
			prime[i*prime[j]]=0;
			if(i%prime[j]==0)
			{
				mu[i*prime[j]]=0;
				phi[i*prime[j]]=phi[i]*prime[j];
				break;
			}
			mu[i*prime[j]]=-mu[i];
			phi[i*prime[j]]=phi[i]*(prime[j]-1);
		}
