void dfs1(int v, int f, int d)
{
    fa[v] = f, dep[v] = d, siz[v] = 1, hvy[v] = -1;
    for (int i = head[v]; i; i = edges[i].next)
    {
        int w = edges[i].to;
        if (w != f)
        {
            dfs1(w, v, d + 1);
            siz[v] += siz[w];
            if (hvy[v] == -1 || siz[hvy[v]] < siz[w])
                hvy[v] = w;
        }
    }
}
int dfn[maxn], top[maxn], timer;
void dfs2(int v, int tp)
{
    dfn[v] = ++timer, top[v] = tp;
    if (hvy[v] != -1)
    {
        dfs2(hvy[v], tp);
        for (int i = head[v]; i; i = edges[i].next)
        {
