	while(ch>47&&ch<58)s=(s<<1)+(s<<3)+(ch^48),ch=getchar();
	return f?-s:s;
}
namespace T3 {//直径 
	int h[N],cnt,to[N<<1],nx[N<<1],fp[N<<1],dep[N],in[N],tot,t2;
