	}
	void Pre() {
		for(int i=1; i<=t2; ++i)rmq[0][i]=node(dep[fp[i]],fp[i]),Log[i]=Log[i>>1]+1;
		for(int j=1; (1<<j)<=t2; ++j)for(int i=1; i+(1<<j)<=t2+1; ++i)rmq[j][i]=min(rmq[j-1][i],rmq[j-1][i+(1<<j-1)]);
	}
	int Lca(int x,int y) {
		if(in[x]<in[y])swap(x,y);int len=Log[in[x]-in[y]+1];
