#define LL long long
using namespace std;
int n,m,l,r,s,e,f;//先把题目中所需的变量都定义上
LL a[MAXN],b[MAXN],c[MAXN],Max,ans;//Max记录最大值，
int main(){
        scanf("%d %d",&n,&m);//输入
        for(int i = 1;i <= m;i ++){
            scanf("%d %d %d %d",&l,&r,&s,&e);//输入
            f = (e - s) / (r - l);//计算公差
            c[l] = c[l] + s;
            c[l+1] = c[l+1] + f - s;//最核心的三行代码
            c[r+1] = c[r+1] - f - e;
            c[r+2] = c[r+2] + e;
        }
        for(int i = 1;i <= n;i ++){
            b[i] = b[i-1] + c[i];
            a[i] = a[i-1] + b[i];
            if(a[i] > Max) Max = a[i];//a数组为最后伤害，Max为最大值
            ans ^= a[i];//计算异或和
        }
        printf("%lld %lld",ans,Max);//输出
        return 0;
    }
