	calrev(lim,len);
	while(bas<=(n<<1)) // edit 1 <=
	{
		cur^=1;
		memset(b[cur],0,sizeof(b[cur]));
		for(int i=0;i<bas;++i)
			b[cur][i]=module(b[cur^1][i]<<1,0);
		mul(b[cur^1],b[cur^1],lim);
		mul(b[cur^1],a,lim);
		for(int i=0;i<bas;++i)
			b[cur][i]=module(b[cur][i],mod-b[cur^1][i]);
		bas<<=1,lim<<=1,++len;
		if(bas<=(n<<1))
