#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>

using namespace std;

const int Mod = 1000000007;
const int N = 18;
const int M = 1 << 18;

int n, m, mx;
int a[N][N];
int ans[N][N];
char s;
int que[2][M], val[2][M];
int f[18][18][M];
int f2[18][18][M];
int tw[N], cnt[2];
int now, last;
int Next[M], head[M];

inline int mod(int a){
	return (a >= Mod ? a - Mod : a);
}

void Zip(int bit, int num, int x, int y){
	long long u = bit % Mod + 1; 
	for(int i = head[u]; i; i = Next[i]){
		if(que[now][i] == bit){
			val[now][i] = mod(val[now][i] + num);
			f[x][y][bit] = val[now][i];
			return;
		}
	}
	Next[++ cnt[now]] = head[u];
	head[u] = cnt[now];
	que[now][cnt[now]] = bit;
	val[now][cnt[now]] = num;
	f[x][y][bit] = num;
}

void dp(){
	cnt[now] = 1;
	val[now][1] = 1;
	que[now][1] = 0;
	f[0][m][0] = 1;
	for(int i = 1; i <= n; ++ i){
		for(int j = 0; j <= mx; ++ j) f[i][0][j << 1] = mod(f[i][0][j << 1] + f[i - 1][m][j]);
		for(int j = 1; j <= cnt[now]; ++ j) que[now][j] <<= 1; 
		for(int j = 1; j <= m; ++ j){
			memset(head, 0, sizeof head);
			last = now; now ^= 1;
			cnt[now] = 0;
			for(int k = 1; k <= cnt[last]; ++ k){
				int bit = que[last][k], num = val[last][k];
				int b1 = (bit >> ((j - 1))) % 2, b2 = (bit >> (j)) % 2;
				if(!a[i][j]){
					if(!b1 && !b2) Zip(bit, num, i, j);
				}
				else if(!b1 && !b2){
					Zip(bit, num, i, j);
					if(a[i + 1][j]) Zip(bit + tw[j - 1], num, i, j);
					if(a[i][j + 1]) Zip(bit + tw[j], num, i, j);
				}
				else if(!b1 && b2) {
					Zip(bit - tw[j], num, i, j);
				}
				else if(b1 && !b2){
					Zip(bit - tw[j - 1], num, i, j);
				}
			}
		}
	}
}

void Zip2(int bit, int num, int x, int y){
	long long u = bit % Mod + 1; 
	for(int i = head[u]; i; i = Next[i]){
		if(que[now][i] == bit){
			val[now][i] = mod(val[now][i] + num);
			f2[x][y][bit] = val[now][i];
			return;
		}
	}
	Next[++ cnt[now]] = head[u];
	head[u] = cnt[now];
	que[now][cnt[now]] = bit;
	val[now][cnt[now]] = num;
	f2[x][y][bit] = num;
}

void dp2(){
	now = 0;
	memset(cnt, 0, sizeof cnt);
	memset(val, 0, sizeof val);
	memset(que, 0, sizeof que);
	memset(head, 0, sizeof head);
	memset(Next, 0, sizeof Next);
	cnt[now] = 1;
	val[now][1] = 1;
	que[now][1] = 0;
	f2[n + 1][1][0] = 1;
	for(int i = n; i >= 1; -- i){
		for(int j = 0; j <= mx; ++ j) f2[i][m + 1][j >> 1] = mod(f2[i][m + 1][j >> 1] + f2[i + 1][1][j]);
		for(int j = 1; j <= cnt[now]; ++ j) que[now][j] >>= 1;
		for(int j = m; j >= 1; -- j){
			memset(head, 0, sizeof head);
			last = now; now ^= 1;
			cnt[now] = 0;
			for(int k = 1; k <= cnt[last]; ++ k){
				int bit = que[last][k], num = val[last][k];
				int b1 = (bit >> (j - 1)) % 2, b2 = (bit >> (j)) % 2;
				if(!a[i][j]){
					if(!b1 && !b2) Zip2(bit, num, i, j);
				}
				else if(!b1 && !b2){
					Zip2(bit, num, i, j);
					if(a[i - 1][j]) Zip2(bit + tw[j], num, i, j);
					if(a[i][j - 1]) Zip2(bit + tw[j - 1], num, i, j);
				}
				else if(b1 && !b2){
					Zip2(bit - tw[j - 1], num, i, j);
				}
				else if(!b1 && b2) {
					Zip2(bit - tw[j], num, i, j);
				}

			}
		}
	}
}

signed main(){
	scanf("%d%d", &n, &m);
	mx = (1 << (m + 1)) - 1;
	for(int i = 1; i <= n; ++ i)
		for(int j = 1; j <= m; ++ j){
			scanf("%d", &a[i][j]);
			a[i][j] ^= 1;
		}
	tw[0] = 1; 
	for(int i = 1; i <= 17; ++ i) tw[i] = tw[i - 1] << 1; //每个下标是个四进制状态。 
	dp(); dp2();

	for(int i = 1; i <= n; ++ i){
		for(int j = 1; j <= m; ++ j){
			if(!a[i][j]) continue;
			int now = mx ^ (1 << (j - 1)) ^ (1 << j);
			for(int k = now; k; k = (k - 1) & now){
				ans[i][j] = mod(ans[i][j] + (1ll * f[i][j - 1][k] % Mod * f2[i][j + 1][k] % Mod) % Mod);
			}
			ans[i][j] = mod(ans[i][j] + (1ll * f[i][j - 1][0] % Mod * f2[i][j + 1][0] % Mod) % Mod);
		}	
	}

	for(int i = 1; i <= n; ++ i){
		for(int j = 1; j <= m; ++ j) printf("%d ", ans[i][j]);
		printf("\n");
	}
	return 0;
}
