#include<bits/stdc++.h>
using namespace std;
map<long long,long long>m;
long long T,n,tmp,had[100010],ans[100010];
void doit()
{
	cin>>n;
	m.clear();
	bool can=false;
	memset(had,0,sizeof(had));
	memset(ans,0,sizeof(ans));
	long long cnt=0,maxn=0,tot=0;
	for(int i=1;i<=n;i++)
	{
		cin>>tmp;
		m[tmp]++;
		if(m[tmp]==1)
			had[++cnt]=tmp;
	}
	for(int i=1;i<=cnt;i++)
		maxn=max(maxn,m[had[i]]);
	for(int i=1;i<=cnt;i++)
		if(m[had[i]]==maxn)
			ans[++tot]=had[i];
		else
			can=true;
	sort(ans+1,ans+1+tot);
	if(can)
	{
		cout<<tot<<endl;
		for(int i=1;i<=tot;i++)
			cout<<ans[i]<<" ";
		cout<<endl;
	}
	else
		cout<<"-1\n";
}
int main()
{
	cin>>T;
	while(T--)
		doit();
	return 0;
