int main()
{
    scanf("%s",kdl);//珂朵莉。 
    f[0]=1;
    f[1]=1;
    f[2]=2;
    for(int i=3;i<=91;i++)//i定义的太大的话，斐波那契数列过大会导致无法输出…… 
    f[i]=f[i-1]+f[i-2];//归纳数列长度！！！注意，是长度！！！（每个第i个数列中大翠香数量为第i-1个数列的长度！） 

    scanf("%lld",&q);

    for(long long int p=1;p<=q;p++)
    {
        scanf("%lld%lld",&a,&b);
        a--;//毕竟是闭区间。 
        for(int i=91;i>=0;i--)//从大到小! 这点需要自己理解，讲不清楚的。
        {
