int n,T,x,ans,flag;
int main(){
	scanf("%d",&T);
	while(T--){
		scanf("%d",&n);
		ans=0;flag=1;
		for(int i=1;i<=n;i++){
			scanf("%d",&x);
			ans^=x;
			flag&=(x==1);
		}
		if(flag==1)ans=!(n&1);
		if(ans==0)printf("Brother\n");
		else printf("John\n");
	}
	return 0;
}
